mapper.select:
select



from sp_order t
where 1=1

    and t.user_id = #{userId}


    and t.order_id = #{orderId}


    and t.pin_active_id = #{pinActiveId}


    and t.order_type = #{orderType}

and order_status in ('PS','S')
order by t.order_create_at DESC

select line_id,
order_id    ,
sku_id      ,
item_id     ,
amount      ,
price       ,
sku_title   ,
sku_img     ,
split_id    ,
sku_size    ,
sku_color   ,
sku_type    ,
sku_type_id,
pin_tiered_price_id
from sp_order_line where 1=1

    and line_id=#{lineId}


    and order_id=#{orderId}


    and sku_id =#{skuId}


    and item_id=#{itemId}


    and split_id=#{splitId}


    and sku_type=#{skuType}


    and sku_type_id=#{skuTypeId}
